import { useState, useCallback, useEffect, useRef } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Send, MessageSquare, Users, Plus, Smartphone, Paperclip, X, Upload, FileText, Image, Video, Music, File, Download, Search, Clock, Phone, Trash2, BarChart3 } from "lucide-react";
import { useLocation } from "wouter";

interface Chat {
  id: string;
  name: string;
  isGroup: boolean;
  unreadCount: number;
  lastMessage: {
    body: string;
    timestamp: number;
    fromMe: boolean;
  } | null;
  timestamp: number;
}

interface Contact {
  id: string;
  name: string;
  number: string;
  isMyContact: boolean;
  isWAContact: boolean;
  profilePicUrl: string | null;
  isGroup: boolean;
}

interface Group {
  id: string;
  name: string;
  isGroup: boolean;
  unreadCount: number;
  lastMessage: {
    body: string;
    timestamp: number;
    fromMe: boolean;
  } | null;
  timestamp: number;
  participants: any[];
}

interface ContactGroup {
  id: string;
  name: string;
  description: string | null;
  totalContacts: number;
  validContacts: number;
  invalidContacts: number;
  duplicateContacts: number;
  createdAt: string;
}

interface BulkCampaign {
  id: string;
  name: string;
  contactGroupId: string;
  message: string;
  status: string;
  sentCount: number;
  failedCount: number;
  createdAt: string;
}

// Country codes list
const countryCodes = [
  { code: "+1", country: "US/Canada", flag: "ðŸ‡ºðŸ‡¸" },
  { code: "+91", country: "India", flag: "ðŸ‡®ðŸ‡³" },
  { code: "+44", country: "UK", flag: "ðŸ‡¬ðŸ‡§" },
  { code: "+49", country: "Germany", flag: "ðŸ‡©ðŸ‡ª" },
  { code: "+33", country: "France", flag: "ðŸ‡«ðŸ‡·" },
  { code: "+61", country: "Australia", flag: "ðŸ‡¦ðŸ‡º" },
  { code: "+81", country: "Japan", flag: "ðŸ‡¯ðŸ‡µ" },
  { code: "+86", country: "China", flag: "ðŸ‡¨ðŸ‡³" },
  { code: "+7", country: "Russia", flag: "ðŸ‡·ðŸ‡º" },
  { code: "+55", country: "Brazil", flag: "ðŸ‡§ðŸ‡·" },
  { code: "+52", country: "Mexico", flag: "ðŸ‡²ðŸ‡½" },
  { code: "+34", country: "Spain", flag: "ðŸ‡ªðŸ‡¸" },
  { code: "+39", country: "Italy", flag: "ðŸ‡®ðŸ‡¹" },
  { code: "+31", country: "Netherlands", flag: "ðŸ‡³ðŸ‡±" },
  { code: "+41", country: "Switzerland", flag: "ðŸ‡¨ðŸ‡­" },
  { code: "+46", country: "Sweden", flag: "ðŸ‡¸ðŸ‡ª" },
  { code: "+47", country: "Norway", flag: "ðŸ‡³ðŸ‡´" },
  { code: "+45", country: "Denmark", flag: "ðŸ‡©ðŸ‡°" },
  { code: "+358", country: "Finland", flag: "ðŸ‡«ðŸ‡®" },
  { code: "+82", country: "South Korea", flag: "ðŸ‡°ðŸ‡·" },
];

export default function Dashboard() {
  const { toast } = useToast();
  const [, setLocation] = useLocation();
  const queryClient = useQueryClient();

  // Handle URL parameters for module selection
  const urlParams = new URLSearchParams(window.location.search);
  const moduleFromUrl = urlParams.get('module');

  // Navigation states
  const [selectedFeature, setSelectedFeature] = useState<'whatsapp' | 'rcs'>('whatsapp');
  const [selectedModule, setSelectedModule] = useState(moduleFromUrl || 'send-message');

  // Form states
  const [countryCode, setCountryCode] = useState("+91");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [message, setMessage] = useState("");
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [isDragOver, setIsDragOver] = useState(false);

  // Contact Groups state
  const [showCreateGroupDialog, setShowCreateGroupDialog] = useState(false);
  const [showBulkMessageDialog, setShowBulkMessageDialog] = useState(false);
  const [newGroupName, setNewGroupName] = useState("");
  const [newGroupDescription, setNewGroupDescription] = useState("");
  const [selectedContactGroup, setSelectedContactGroup] = useState("");
  const [bulkMessage, setBulkMessage] = useState("");
  const wsRef = useRef<WebSocket | null>(null);

  // Update field values for placeholder visibility
  const fieldValues = {
    phoneNumber,
    message,
    newGroupName,
    newGroupDescription,
    bulkMessage
  };

  // Fetch current session info
  const { data: sessionInfo } = useQuery<{
    name: string;
    loginTime: string;
  }>({
    queryKey: ['/api/session-info'],
    refetchInterval: 10000, // Check every 10 seconds
  });

  // Fetch contact groups
  const { data: contactGroups = [], isLoading: contactGroupsLoading } = useQuery<ContactGroup[]>({
    queryKey: ['/api/contact-groups'],
    refetchInterval: 30000,
  });

  // Fetch chats
  const { data: chats = [], isLoading: chatsLoading } = useQuery<Chat[]>({
    queryKey: ['/api/chats'],
    enabled: !!sessionInfo,
    refetchInterval: 30000,
  });

  // Fetch contacts  
  const { data: contacts = [], isLoading: contactsLoading } = useQuery<Contact[]>({
    queryKey: ['/api/contacts'],
    enabled: !!sessionInfo,
    refetchInterval: 30000,
  });

  // Fetch groups
  const { data: groups = [], isLoading: groupsLoading } = useQuery<Group[]>({
    queryKey: ['/api/groups'],
    enabled: !!sessionInfo,
    refetchInterval: 30000,
  });

  // Fetch bulk campaigns
  const { data: bulkCampaigns = [], isLoading: campaignsLoading } = useQuery<BulkCampaign[]>({
    queryKey: ['/api/bulk-campaigns'],
    refetchInterval: 30000,
  });

  // WebSocket connection for real-time updates
  useEffect(() => {
    // Skip WebSocket in development mode to avoid conflicts with Vite HMR
    if (import.meta.env.DEV) {
      console.log('Skipping WebSocket in development mode');
      return;
    }

    const protocol = window.location.protocol === "https:" ? "wss:" : "ws:";
    const wsUrl = `${protocol}//${window.location.host}/ws`;

    const connectWebSocket = () => {
      try {
        wsRef.current = new WebSocket(wsUrl);

        wsRef.current.onopen = () => {
          console.log('ðŸ“¡ WebSocket connected to', wsUrl);
        };

        wsRef.current.onmessage = (event) => {
          try {
            const data = JSON.parse(event.data);
            console.log('ðŸ“¨ WebSocket message received:', data.type);

            switch (data.type) {
              case 'qr':
                // Invalidate QR-related queries to fetch new QR
                queryClient.invalidateQueries({ queryKey: ['/api/get-qr'] });
                break;
              case 'connected':
                // Invalidate session info when connected
                queryClient.invalidateQueries({ queryKey: ['/api/session-info'] });
                break;
              case 'disconnected':
              case 'logout':
                // Invalidate all session-related queries
                queryClient.invalidateQueries({ queryKey: ['/api/session-info'] });
                queryClient.invalidateQueries({ queryKey: ['/api/get-qr'] });
                break;
            }
          } catch (error) {
            console.error('Failed to parse WebSocket message:', error);
          }
        };

        wsRef.current.onclose = (event) => {
          console.log('ðŸ“¡ WebSocket disconnected:', event.code, event.reason);
          // Reconnect after 3 seconds
          setTimeout(connectWebSocket, 3000);
        };

        wsRef.current.onerror = (error) => {
          console.error('ðŸ“¡ WebSocket error:', error);
        };
      } catch (error) {
        console.error('Failed to create WebSocket connection:', error);
        // Retry after 5 seconds
        setTimeout(connectWebSocket, 5000);
      }
    };

    connectWebSocket();

    // Cleanup on unmount
    return () => {
      if (wsRef.current && wsRef.current.readyState === WebSocket.OPEN) {
        wsRef.current.close();
      }
    };
  }, [queryClient]);

  // Send message mutation
  const sendMessageMutation = useMutation({
    mutationFn: (data: { phoneNumber: string; message: string; file?: File }) => {
      if (data.file) {
        // Send media message
        const formData = new FormData();
        formData.append('phoneNumber', data.phoneNumber);
        formData.append('message', data.message);
        formData.append('media', data.file);

        return fetch('/api/send-media-message', {
          method: 'POST',
          body: formData,
        }).then(res => {
          if (!res.ok) throw new Error('Failed to send media message');
          return res.json();
        });
      } else {
        // Send text message
        return apiRequest("/api/send-message", "POST", data);
      }
    },
    onSuccess: () => {
      toast({
        title: "Message Sent",
        description: selectedFile ? "Your media message has been sent successfully!" : "Your message has been sent successfully!",
      });
      setPhoneNumber("");
      setMessage("");
      setSelectedFile(null);
    },
    onError: (error: any) => {
      toast({
        title: "Failed to Send",
        description: error.message || "Failed to send message",
        variant: "destructive",
      });
    },
  });

  const handleSendMessage = () => {
    if (!sessionInfo) {
      toast({
        title: "Not Connected",
        description: "Please connect to WhatsApp first",
        variant: "destructive",
      });
      return;
    }

    if (!phoneNumber.trim()) {
      toast({
        title: "Missing Information",
        description: "Please enter phone number",
        variant: "destructive",
      });
      return;
    }

    if (!message.trim() && !selectedFile) {
      toast({
        title: "Missing Content",
        description: "Please enter a message or select a file to send",
        variant: "destructive",
      });
      return;
    }

    // Combine country code with phone number
    const fullPhoneNumber = phoneNumber.startsWith('+') ? phoneNumber.trim() : `${countryCode}${phoneNumber.trim()}`;

    sendMessageMutation.mutate({
      phoneNumber: fullPhoneNumber,
      message: message.trim(),
      file: selectedFile || undefined,
    });
  };

  const handleFileSelect = useCallback((event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      setSelectedFile(file);
    }
  }, []);

  const removeSelectedFile = useCallback(() => {
    setSelectedFile(null);
  }, []);

  const handleDragOver = useCallback((event: React.DragEvent) => {
    event.preventDefault();
    setIsDragOver(true);
  }, []);

  const handleDragLeave = useCallback((event: React.DragEvent) => {
    event.preventDefault();
    setIsDragOver(false);
  }, []);

  const handleDrop = useCallback((event: React.DragEvent) => {
    event.preventDefault();
    setIsDragOver(false);
    const file = event.dataTransfer.files[0];
    if (file) {
      setSelectedFile(file);
    }
  }, []);

  // Create Contact Group mutation
  const createContactGroupMutation = useMutation({
    mutationFn: (data: { name: string; description: string }) =>
      apiRequest("/api/contact-groups", "POST", data),
    onSuccess: () => {
      toast({
        title: "Contact Group Created",
        description: "Your contact group has been created successfully!",
      });
      setShowCreateGroupDialog(false);
      setNewGroupName("");
      setNewGroupDescription("");
      queryClient.invalidateQueries({ queryKey: ['/api/contact-groups'] });
    },
    onError: (error: any) => {
      toast({
        title: "Failed to Create",
        description: error.message || "Failed to create contact group",
        variant: "destructive",
      });
    },
  });

  // Delete Contact Group mutation
