                  <Label 
                    htmlFor="password" 
                    className={`absolute left-3 pointer-events-none transition-all duration-200 ${
                      fieldValues.password
                        ? "hidden"
                        : "top-3 text-sm text-gray-500 dark:text-gray-400"
                    }`}
                  >
                    Password
                  </Label>
                  <button
